<template>
  <table>
    <thead>
      <tr>
        <th>#</th>
        <th>Club</th>
        <th>Form</th>
        <th>GP</th>
        <th>W</th>
        <th>D</th>
        <th>L</th>
        <th>GF</th>
        <th>GA</th>
        <th>GD</th>
        <th>Pts</th>
      </tr>
    </thead>
    <tbody>
      <tr v-for="(item, index) in data" :key="index" >
        <td >{{ item.intRank }}</td>
        <td >
          <div>
            <img v-if="item.strTeamBadge" :src="item.strTeamBadge" alt="Team Badge" /> {{ item.strTeam }}
          </div>
        </td>
        <td >
          <div>
            <img
              v-for="(form, index) in item.strForm?.split('')"
              :key="index"
              :src="getResultBadge(form)"
              :alt="getResultBadgeAlt(form)"
            />
          </div>
        </td>
        <td>{{ item.intPlayed }}</td>
        <td>{{ item.intWin }}</td>
        <td>{{ item.intDraw }}</td>
        <td>{{ item.intLoss }}</td>
        <td>{{ item.intGoalsFor }}</td>
        <td>{{ item.intGoalsAgainst }}</td>
        <td>{{ item.intGoalDifference }}</td>
        <td>{{ item.intPoints }}</td>
      </tr>
    </tbody>
  </table>
</template>

<script>
import mockApi from '../mock-api/index.js';

const matchResult = {
  W: {
    img: 'data:image/svg+xml;base64,',
    alt: 'Win',
  },
  D: {
    img: 'data:image/svg+xml;base64,',
    alt: 'Draw',
  },
  L: {
    img: 'data:image/svg+xml;base64,',
    alt: 'Loss',
  },
};

export default {
	data() {
		return {
			data: [],
		};
	},
	async created() {
		const apiData = await mockApi();
		this.data = apiData.table;
	},
  methods: {
    getResultBadge(result) {
      return matchResult[result].img;
    },
    getResultBadgeAlt(result) {
      return matchResult[result].alt;
    },
  },
};
</script>
